import React from 'react';
import {Link} from "react-router-dom"
import logo from "../navbar/logo_transparent.png";
import "./Home.scss"

function Home(props) {
console.log(props.games)
    return (

          <div>
            <header className="logo">
            <img src={logo} alt="" width="500" heith="300"/>
            </header>

        {/* SECTION GAMES */}
        <div className="all">
        <div className="games">
          <h3>our best offers Games</h3>

          {
            props.games.map(game =>(
            <Link to={"game/"+game._id}>

              <div className="infoGame">
                  <img className="infoImg"src={game.imageUrl} alt=""/>
                  <div>
                  <h4>{game.title}</h4>
                  </div>
              </div>
            </Link>

          ))}
        </div>


      {/* SECTION CONSOLES */}

      <div className="consoles" >
        <h3>our best deals on consoles</h3>
          {props.consoles.map(consol => (
              <Link to={"consoles/"+consol._id}>
                <div className="infoGame">
                  <img className="infoImg" src={consol.imageUrl} alt=""/>
                  <div>
                  <h4>{consol.consoleModel}</h4>
                  </div>
              </div>
              </Link>
          ))}
        </div>
      </div>


       </div>

    )
}

export default Home;
